        let jY = nativeint (info.DY - info.SX * info.DX) * sa
        let sX = nativeint (info.SX * info.DX) * sa
        let jX = nativeint (info.DX) * sa
        let initialCoord = V2l.Zero
        let step = V2l.One
        let mutable coord = initialCoord
