                ptr <- ptr + jX
            coord.Y <- coord.Y + step.Y
            ptr <- ptr + jY
    member x.SetByCoord(value : V2l -> 'a) = 
        let cXY = compare (abs info.DX) (abs info.DY)
        if cXY >= 0  then x.SetByCoordXY(value)
        else x.SetByCoordYX(value)
